import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [data, setData] = React.useState();
  const [filters, setFilters] = useState({
    role: "",
    numberOfEmployees: "",
    experience: "",
    remote: "",
    minBasePay: "",
    companyName: ""
  });

  useEffect(() => {
    const fetchData = async () => {
      const myHeaders = new Headers();
      myHeaders.append("Content-Type", "application/json");
      const body = JSON.stringify({
        limit: 1000,
        offset: 0,
      });

      const requestOptions = {
        method: "POST",
        headers: myHeaders,
        body,
      };

      try {
        const response = await fetch(
          "https://api.weekday.technology/adhoc/getSampleJdJSON",
          requestOptions
        );
        if (!response.ok) {
          throw new Error("Network response was not ok.");
        }
        const result = await response.json();
        setData(result);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const filteredData = data?.jdList.filter(item => {
    return (
      (filters.role ? item.jobRole === filters.role : true) &&
      (filters.numberOfEmployees ? item.numberOfEmployees === filters.numberOfEmployees : true) &&
      (filters.experience ? item.experience === filters.experience : true) &&
      (filters.remote ? item.remote === filters.remote : true) &&
      (filters.minBasePay ? item.minBasePay === filters.minBasePay : true) &&
      (filters.companyName ? item.companyName.toLowerCase().includes(filters.companyName.toLowerCase()) : true)
    );
  });

  return (
    <div className="App">
      <RoleDropdown setFilters={setFilters} />
      {filteredData &&
        filteredData.map((item) => {
          return <Card key={item.jdUid} data={item} />;
        })}
    </div>
  );
}

const Card = ({ data }) => {
  const {
    companyName,
    jobRole,
    maxExp,
    minExp,
    maxJdSalary,
    minJdSalary,
    salaryCurrencyCode,
    location,
    logoUrl,
    jobDetailsFromCompany,
    jdLink,
    skills,
  } = data;

  return (
    <div className="card-container">
      <div className="card-post">
        <span>⏳ Posted 4 days ago</span>
      </div>
      <div className="card-info">
        <div className="cmp-logo">
          <img src={logoUrl} width={25} height={40} alt="company-logo" />
        </div>
        <div className="cmp-des">
          <div className="comp-name">{companyName}</div>
          <div className="comp-position">{jobRole}</div>
          <div className="comp-loc">
            {location} {minExp && maxExp && `| Exp: ${minExp}-${maxExp} years`}
          </div>
        </div>
      </div>
      <div className="card-salary">
        Estimated Salary: {salaryCurrencyCode}
        {minJdSalary} - {maxJdSalary} LPA ✅
      </div>

      <div className="cmp-about">About Company:</div>

      <div className="cmp-about-us">About us:</div>

      <div className="cmp-desc">{jobDetailsFromCompany}</div>

      <div className="cmp-link">
        <a href={jdLink}>View job</a>
      </div>

      {skills && (
        <div>
          <div className="cmp-skills">Skills</div>
          <div className="skill-container">
            {skills.map((skill, index) => (
              <div key={index} className="skill-item">
                {skill}
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="min-exp">Minimum Experience</div>
      <div className="cmp-exp">{minExp} years</div>

      <div className="btn">
        <button className="apply-btn">⚡ Easy Apply</button>
      </div>
    </div>
  );
};

const RoleDropdown = ({ setFilters }) => {
  const [selectedOption, setSelectedOption] = React.useState("");
  const companyRoles = [
    {
      section: "Engineering",
      roles: [
        "Backend",
        "Frontend",
        "Fullstack",
        "IOS",
        "Flutter",
        "React Native",
        "Android",
        "Tech Lead",
        "Dev-Ops",
        "Data-Engineer",
      ],
    },
    {
      section: "HR",
      roles: ["Hr"],
    },
    {
      section: "Marketing",
      roles: ["Digital Marketing Manager", "Growth Hacker", "Marketing"],
    },
    {
      section: "Finance",
      roles: ["Finance"],
    },
    {
      section: "Legal",
      roles: ["Legal"],
    },
    {
      section: "Sales",
      roles: [
        "Sales Manager",
        "Sales Representative",
        "Business Development Executive",
      ],
    },
    {
      section: "Management",
      roles: ["Management"],
    },
    {
      section: "Business Analyst",
      roles: ["Business Analyst"],
    },
  ];

  const handleDropdownChange = (e) => {
    setSelectedOption(e.target.value);
    setFilters(prevFilters => ({ ...prevFilters, role: e.target.value }));
  };

  return (
    <div className="dropdown-container">
      <label htmlFor="role-dropdown">Roles</label>
      <select
        id="role-dropdown"
        value={selectedOption}
        onChange={handleDropdownChange}
      >
        <option value="">Select Role</option>
        {companyRoles.map((section) => (
          <optgroup key={section.section} label={section.section}>
            {section.roles.map((role) => (
              <option key={role} value={role}>
                {role}
              </option>
            ))}
          </optgroup>
        ))}
      </select>
    </div>
  );
};

export default App;
