import React from "react";
import "./App.css";

function App() {
  const [data, setData] = React.useState();

  React.useEffect(() => {
    const myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");
    const body = JSON.stringify({
      limit: 10,
      offset: 0,
    });

    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body,
    };

    fetch(
      "https://api.weekday.technology/adhoc/getSampleJdJSON",
      requestOptions
    )
      .then((response) => response.json())
      .then((result) => setData(result))
      .catch((error) => console.error(error));
  }, []);

  // console.log(data?.jdList[0]);

  return (
    <div className="App">
      {data?.jdList &&
        data.jdList.map((item) => {
          return <Card key={item.jdUid} data={item} />;
        })}
    </div>
  );
}

export default App;

const Card = ({ data }) => {

  // if(!item) return null;
  const { companyName, jobRole, maxExp, minExp } =
    data;

  return (
    <div className="card-container">
      <div className="card-post">
        <span>⏳ Posted 4 days ago</span>
      </div>
      <div className="card-info">
        <div className="cmp-logo">
          <img
            src="https://logo.clearbit.com/adobe.com"
            width={25}
            height={40}
            alt="company-logo"
          />
        </div>
        <div className="cmp-des">
          <div className="comp-name">{companyName}</div>
          <div className="comp-position">{jobRole}</div>
          <div className="comp-loc">
            India | Exp: {minExp}-{maxExp} years
          </div>
        </div>
      </div>
      <div className="card-salary">
        Estimated Salary: ₹{} - {} LPA I
      </div>

      <div className="cmp-about">About Company:</div>

      <div className="cmp-about-us">About us:</div>

      <div className="cmp-desc">
        This is a sample job and you must have displayed it to understand that
        its not just some random lorem ipsum text but something which was
        manually written. Oh well, if random text is what you were looking for
        then here it is: Lorem Ipsum is simply dummy text of the printing and
        typesetting industry. Lorem Ipsum has been the industry's standard dummy
        text ever since the 1500s, when an unknown printer took a galley of type
        and scrambled it to make a type specimen book. It has survived not only
        five centuries, but also the leap into electronic typesetting, remaining
        essentially unchanged. It was popularised in the 1960s with the release
        of Letraset sheets containing Lorem Ipsum passages and now in this
        assignment.
      </div>

      <div className="cmp-link">
        <a href="https://weekday.works">View job</a>
      </div>

      <div className="cmp-skills">Skills</div>
      <div className="skill-container">
        <div className="skill-item">Typescript</div>
      </div>

      <div className="min-exp">Minimum Experience</div>
      <div className="cmp-exp">5 years</div>

      <div className="btn">
        <button className="apply-btn">⚡ Easy Apply</button>
      </div>
    </div>
  );
};
