import logo from "./logo.svg";
import React from "react";
import "./App.css";

function App() {
  React.useEffect(() => {
    const myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");
    const body = JSON.stringify({
      limit: 10,
      offset: 0,
    });

    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body,
    };

    fetch(
      "https://api.weekday.technology/adhoc/getSampleJdJSON",
      requestOptions
    )
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.error(error));
  }, []);

  return (
    <div className="App">
      <div className="card-container">
        <div className="card-post">
          <span>Posted 4 days ago</span>
        </div>
        <div className="card-info">
          <div className="cmp-logo">
            <img src="https://logo.clearbit.com/adobe.com" width={25} height={40} alt="company-logo" />
          </div>
          <div className="cmp-des">
            <div className="comp-name">FlexWash Technologies</div>
            <div className="comp-position">Senior Engineer</div>
            <div className="comp-loc">India | Exp: 5-5 years</div>
          </div>
        </div>
        <div className="card-salary">Estimated Salary: $30 - 60 LPA I</div>

        <div className="cmp-about">About Company:</div>

        <div className="cmp-about-us">About us:</div>

        <div className="cmp-desc">
        This is a sample job and you must have displayed it to understand that its not just some random lorem ipsum text but something which was manually written. Oh well, if random text is what you were looking for then here it is: Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages and now in this assignment.
        </div>

        <a className="cmp-link" href="https://weekday.works">View job</a>
      </div>
    </div>
  );
}

export default App;
