import React from "react";
import "./App.css";

function App() {
  const [data, setData] = React.useState();

  React.useEffect(() => {
    const myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");
    const body = JSON.stringify({
      // limit: 10,
      offset: 0,
    });

    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body,
    };

    fetch(
      "https://api.weekday.technology/adhoc/getSampleJdJSON",
      requestOptions
    )
      .then((response) => response.json())
      .then((result) => setData(result))
      .catch((error) => console.error(error));
  }, []);

  return (
    <div className="App">
      <RoleDropdown />
      {data?.jdList &&
        data.jdList.map((item) => {
          return <Card key={item.jdUid} data={item} />;
        })}
    </div>
  );
}

export default App;

const Card = ({ data }) => {
  const {
    companyName,
    jobRole,
    maxExp,
    minExp,
    maxJdSalary,
    minJdSalary,
    salaryCurrencyCode,
    location,
    logoUrl,
    jobDetailsFromCompany,
    jdLink,
    skills,
  } = data;

  return (
    <div className="card-container">
      <div className="card-post">
        <span>⏳ Posted 4 days ago</span>
      </div>
      <div className="card-info">
        <div className="cmp-logo">
          <img src={logoUrl} width={25} height={40} alt="company-logo" />
        </div>
        <div className="cmp-des">
          <div className="comp-name">{companyName}</div>
          <div className="comp-position">{jobRole}</div>
          <div className="comp-loc">
            {location} {minExp && maxExp && `| Exp: ${minExp}-${maxExp} years`}
          </div>
        </div>
      </div>
      <div className="card-salary">
        Estimated Salary: {salaryCurrencyCode}
        {minJdSalary} - {maxJdSalary} LPA ✅
      </div>

      <div className="cmp-about">About Company:</div>

      <div className="cmp-about-us">About us:</div>

      <div className="cmp-desc">{jobDetailsFromCompany}</div>

      <div className="cmp-link">
        <a href={jdLink}>View job</a>
      </div>

      {skills && (
        <div>
          <div className="cmp-skills">Skills</div>
          <div className="skill-container">
            {skills.map((skill, index) => (
              <div key={index} className="skill-item">
                {skill}
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="min-exp">Minimum Experience</div>
      <div className="cmp-exp">{minExp} years</div>

      <div className="btn">
        <button className="apply-btn">⚡ Easy Apply</button>
      </div>
    </div>
  );
};

const RoleDropdown = () => {
  const [selectedOption, setSelectedOption] = React.useState("");
  const companyRoles = [
    {
      section: "Engineering",
      roles: ["Backend", "Frontend", "Fullstack", "IOS", "Flutter", "React Native", "Android", "Tech Lead", "Dev-Ops"]
    },
    {
      section: "HR",
      roles: ["HR Manager", "Recruiter", "Training Specialist"]
    },
    {
      section: "Marketing",
      roles: ["Marketing Manager", "Marketing Analyst", "Content Writer"]
    },
    {
      section: "Finance",
      roles: ["Financial Analyst", "Accountant", "Finance Manager"]
    },
    {
      section: "Operations",
      roles: ["Operations Manager", "Supply Chain Specialist", "Logistics Coordinator"]
    },
    {
      section: "Sales",
      roles: ["Sales Manager", "Sales Representative", "Business Development Executive"]
    },
    {
      section: "Customer Service",
      roles: ["Customer Service Manager", "Customer Support Representative"]
    },
    {
      section: "IT",
      roles: ["IT Manager", "System Administrator", "Network Engineer"]
    },
  ];


  return (
    <div className="dropdown-wrapper">
      <div className="dropdown-container">
        <label htmlFor="experience-dropdown">Roles</label>
        <select
          id="experience-dropdown"
          value={selectedOption}
          onChange={(e) => setSelectedOption(e.target.value)}
        >
          {companyRoles.map((section) => (
            <optgroup key={section.section} label={section.section}>
              {section.roles.map((role) => (
                <option key={role} value={role}>
                  {role}
                </option>
              ))}
            </optgroup>
          ))}
        </select>
      </div>

      <div className="dropdown-container">
        <label htmlFor="experience-dropdown">Number of Employees</label>
        <select
